//Import tools
import React, { useState } from 'react'

//Import image
import toggleArrow from '../../assets/chevron-solid.svg'

// Import styles
import './collapse.scss';

//Component
function Collapse({ title, icon=null, content, className }) {
	const [open, setOpen] = useState(false)
	function toggle() {
	  setOpen(!open)
	}

	const iconElement = icon && React.isValidElement(icon) ? icon : null;

	return (
	  <div className={className}>
		<button className="collapse__title-container" onClick={toggle}>
		  <h2 className="collapse__title-container__title">{title}</h2>
		  <img
			className={!open ? 'expand_arrow' : 'expand_arrow expand_arrow--opened'}
			src={toggleArrow}
			alt="Cliquez-ici pour dÃ©rouler le texte"
		  />
		</button>
			{open && (
			<div className="collapse__text">
				{React.cloneElement(iconElement, { className: 'collapse__icon' })}
				<br/>
				{content}
			</div>
			)}
	  </div>
	)
  }

  export default Collapse
