import React, { useState } from "react";
import Modal from "react-modal";
import { Document, Page } from 'react-pdf';
import "./cv.scss";


function CvPreview() {
    // State variable for managing zoomed image
  const [zoomedImage, setZoomedImage] = useState(null);
  // Function to open zoomed image
  const openZoomedImage = (imageUrl) => {
    setZoomedImage(imageUrl);
  };
  // Function to close zoomed image
  const closeZoomedImage = () => {
    setZoomedImage(null);
  };
  return (
    <>
      {/* Render the images */}
      {images.map((item, index) => (
        <div key={item.id} onClick={() => openZoomedImage(item.imageUrl)}>
          <Image src={item.imageUrl} alt="image" layout="fill" objectFit="cover" />
        </div>
      ))}
      {/* Render the zoomed image */}
      {zoomedImage && (
        <div className="zoomed-image-container" onClick={closeZoomedImage}>
          <Image src={zoomedImage} alt="zoomed-image" layout="fill" objectFit="contain" />
        </div>
      )}
    </>
  );
};

  export default CvPreview;
