import React, { useState } from "react";
import Modal from "react-modal";
import { Document, Page } from "react-pdf";
import "./cv.scss";

// Set the app element for accessibility
Modal.setAppElement("#root");

function CvPreview() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [pageNumber, setPageNumber] = useState(1); // To control which page to display

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="cv-container">
      {/* PDF preview (using first page of PDF as preview) */}
      <div className="cv-preview" onClick={openModal}>
        <Document
          file="/assets/CV_SOLER_2024_Accenture.pdf"
          onLoadSuccess={({ numPages }) => setPageNumber(1)} // Show the first page
        >
          <Page pageNumber={1} width={300} />
        </Document>
      </div>

      {/* Modal with full PDF */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        className="cv-modal-content"
        overlayClassName="cv-modal-overlay"
      >
        <div className="cv-modal">
          <Document
            file="/assets/CV_SOLER_2024_Accenture.pdf"
            onLoadSuccess={({ numPages }) => setPageNumber(numPages)} // Get the number of pages
          >
            <Page pageNumber={pageNumber} width={600} />
          </Document>

          <div className="cv-modal-actions">
            <a
              href="/assets/CV_SOLER_2024_Accenture.pdf"
              download
              className="cv-download-button"
            >
              Télécharger
            </a>
          </div>

          <button onClick={closeModal} className="cv-close-button">
            Fermer
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default CvPreview;
