import React from "react";
import "./cv.scss";

// Images
import linkedinicon from "../../assets/linkedinicon.png";
import gmailicon from "../../assets/gmailicon.png";
import quotesicon from "../../assets/quotesicon.png";
import CVPreview from "../../assets/CV_SOLER_2024_Accenture.jpg";

function CvPreview() {

      const handlePreview = () => {
        // Ouvre le PDF dans un nouvel onglet
        window.open("https://portfolio-flo-bucket.s3.eu-north-1.amazonaws.com/CV_SOLER_2024_Accenture.pdf", "_blank");
    };
  return (
    <div className="cv-preview">
            <div className="contact-icons">
                <a href="https://linkedin.com/in/florasoler" target="_blank" rel="noreferrer" >
              <img src={linkedinicon} className="icon-button" 
              />
                </a>
                <a href="mailto:floralu.soler@gmail.com" target="_blank">
              <img src={gmailicon} className="icon-button" 
              />
                </a>
            </div>
            <div className="cv-container">
                <img 
                    onClick={handlePreview} 
                    src={CVPreview} 
                    alt="CV Preview" 
                    className="cv-image"
                />
      <div className="text-icons">
        <div className="text">
      <img src={quotesicon} className="quotes" 
      />
      <p className="intro-text"><i>Ma passion des langues étrangères a guidé mes expériences professionnelles au fil des voyages et m’amène à présent aux langages informatiques. <br/><br/>Actuellement alternante chez <a href="https://www.accenture.com/fr-fr" target="_blank"><strong>Accenture</strong></a>, mon but est d’évoluer en tant que développeuse JAVA.</i>
        </p>
        </div>
      </div>

    </div>

    </div>

  );
}

export default CvPreview;
