import React, { useState } from "react";
import Modal from "react-modal";
import { Document, Page } from 'react-pdf';
import "./cv.scss";

// Set the app element for accessibility
Modal.setAppElement("#root");

function CvPreview() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
      setIsModalOpen(true);
    };

    const closeModal = () => {
      setIsModalOpen(false);
    };

    return (
      <div className="cv-container">
        {/* Image cliquable */}
        <img
          src="/assets/CV_SOLER_2024_Accenture.jpg"
          alt="CV Preview"
          className="cv-image"
          onClick={openModal}
        />

        {/* Modale */}
        <Modal
          isOpen={isModalOpen}
          onRequestClose={closeModal}
          className="cv-modal-content"
          overlayClassName="cv-modal-overlay"
        >
          <div className="cv-modal">
            <img
              src="/assets/CV_SOLER_2024_Accenture.jpeg"
              alt="CV Full View"
              className="cv-modal-image"
            />
            <button onClick={closeModal} className="cv-close-button">
              Fermer
            </button>
          </div>
        </Modal>
      </div>
    );
  }

  export default CvPreview;
