import React, { useState } from "react";

// Import components
import Banner from "../../components/Banner/Banner";
import MessageDisplay from "../../components/MessageDisplay/MessageDisplay"; 
// Import image
import contactBanner from "../../assets/contactbanner.jpg";

// Import style
import "./vosimpressions.scss";

function Vosimpressions() {
  const [formData, setFormData] = useState({
    name: "",
    message: "",
  });
  const [successMessage, setSuccessMessage] = useState("");
  const [submittedMessage, setSubmittedMessage] = useState(null); // Stocke les données soumises

  function handleChange(event) {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  }

  function handleSubmit(event) {
    event.preventDefault();

    // Validation : vérifier que tous les champs sont remplis
    if (formData.name && formData.message) {
      setSubmittedMessage({ ...formData }); // Stocke les données soumises
      setSuccessMessage("Votre message a bien été envoyé !");
      setFormData({ name: "", message: "" }); // Réinitialise les champs du formulaire
    } else {
      setSuccessMessage("Veuillez remplir tous les champs.");
    }
  }

  function handleDelete() {
    setSubmittedMessage(null); // Supprime le message affiché
    setSuccessMessage(""); // Réinitialise le message de succès
  }

  return (
    <div>
      {/* Bannière */}
      <Banner picture={contactBanner} title="" className="banner" />

      {/* Formulaire de contact */}
      <div className="contact-container">
        <h2>Laissez un avis!</h2>
        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Nom</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Entrez votre nom"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleChange}
              placeholder="Écrivez votre message ici"
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Envoyer
          </button>
        </form>

        {successMessage && <p className="success-message">{successMessage}</p>}

        {/* Affichage des messages envoyés */}
        {submittedMessage && (
          <MessageDisplay
            name={submittedMessage.name}
            message={submittedMessage.message}
            onDelete={handleDelete}
          />
        )}
      </div>
    </div>
  );
}

export default Vosimpressions;
