import React from "react";
import { Worker } from "@react-pdf-viewer/core"; // Worker for PDF rendering
import { Viewer } from "@react-pdf-viewer/core";
import { toolbarPlugin } from "@react-pdf-viewer/toolbar";
import "@react-pdf-viewer/core/lib/styles/index.css";
import "@react-pdf-viewer/default-layout/lib/styles/index.css";
import "./cv.scss";

function CvPreview() {
  const pdfUrl = "/assets/CV_SOLER_2024_Accenture.pdf"; // Path to your PDF

  // Function to trigger download
  const handleDownload = () => {
    const link = document.createElement("a");
    link.href = pdfUrl;
    link.download = "CV_SOLER_2024_Accenture.pdf";
    link.click();
  };

  // Toolbar plugin for better PDF interaction
  const toolbarPluginInstance = toolbarPlugin();

  return (
    <div className="cv-container">
      <h2>Aperçu du CV</h2>

      {/* PDF Viewer */}
      <Worker workerUrl={`https://unpkg.com/pdfjs-dist/build/pdf.worker.min.js`}>
        <div className="pdf-viewer">
          <Viewer
            fileUrl={pdfUrl}
            plugins={[toolbarPluginInstance]} // Include toolbar for navigation
          />
        </div>
      </Worker>

      {/* Download Button */}
      <button onClick={handleDownload} className="download-btn">
        Télécharger en PDF
      </button>
    </div>
  );
}

export default CvPreview;
