import React, { useState } from "react";
import Modal from "react-modal";
import "./cv.scss";

Modal.setAppElement("#root"); // Assure l'accessibilité

function CvPreview() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="cv-container">
      {/* Aperçu simple du CV */}
      <div className="cv-preview" onClick={openModal}>
        <iframe
          src="/assets/CV_SOLER_2024 Accenture.pdf"
          title="CV Preview"
          className="cv-iframe"
        ></iframe>
      </div>

      {/* Modale */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        className="cv-modal-content"
        overlayClassName="cv-modal-overlay"
      >
        <div className="cv-modal">
          <iframe
            src="/assets/CV_SOLER_2024 Accenture.pdf"
            title="CV Full View"
            className="cv-modal-iframe"
          ></iframe>
          <div className="cv-modal-actions">
            <a
              href="/assets/CV_SOLER_2024 Accenture.pdf"
              download
              className="cv-download-button"
            >
              Télécharger
            </a>
          </div>
          <button onClick={closeModal} className="cv-close-button">
            Fermer
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default CvPreview;
